import './App.css';
import React, {Component} from 'react';
import Person from './Person/Number';

class App extends Component {

  state = {
    people: [
      {name: "Eugenu", age:23},
      {name: "Maksim", age:22},
      {name: "Halim", age:23},
      {name: "Nastya", age:24}
    ]
  }

  changename = () => {
    let people = this.state.people;
    people[1].name = "John";
    this.setState({people});
  };

  increaseage = () => {
    let people = this.state.people.map((person) => {
      return {
        name: person.person,
        age: person.age + 1
      };
    });
    this.setState({people});
  }

  render() {
    return (
      <div className="App">
      <div><button onClick={this.changename}> Change name </button></div>
      <div><button onClick={this.increaseage}> Increase age </button></div>
        <header className="App-header">
          <Person name={this.state.people[0].name} age={this.state.people[0].age}> Hobby: Dota 2 </Person>
          <Person name={this.state.people[1].name} age={this.state.people[1].age}> Hobby: Drinking Viski</Person>
          <Person name={this.state.people[2].name} age={this.state.people[2].age}> Hobby: Economics </Person>
          <Person name={this.state.people[3].name} age={this.state.people[3].age}> Hobby: Beer</Person>
        </header>
      </div>
    );
  }

}

export default App;
